import { useMemo } from 'react';

function progress(checked, items) {
  const total = items.length;
  const done = items.filter(it => checked[it.id]).length;
  return { total, done, pct: Math.round((done / (total || 1)) * 100) };
}

export default function RoadmapChecklist({ roadmap, checked, onToggle, onExport, onImport }) {
  const flatItems = useMemo(() => roadmap.categories.flatMap(c => c.items), [roadmap]);
  const { total, done, pct } = progress(checked, flatItems);

  return (
    <div style={{ marginTop: 24 }}>
      <h3>{roadmap.goal}</h3>
      <p>Estimated timeframe: {roadmap.timeframe_months} months</p>
      <p><strong>{done}/{total}</strong> completed ({pct}%).</p>

      <div style={{ display: 'flex', gap: 8, margin: '8px 0' }}>
        <button onClick={onExport}>Export (JSON)</button>
        <label style={{ display: 'inline-flex', alignItems: 'center', gap: 8, cursor: 'pointer' }}>
          <input type="file" accept="application/json" onChange={onImport} style={{ display: 'none' }} />
          <span>Import (JSON)</span>
        </label>
      </div>

      {roadmap.categories.map(cat => (
        <div key={cat.name} className="card" style={{ padding: 16, margin: '16px 0' }}>
          <h4>{cat.name}</h4>
          <ul style={{ paddingLeft: 0 }}>
            {cat.items.map(it => (
              <li key={it.id} style={{ listStyle: 'none', margin: '8px 0' }}>
                <label>
                  <input
                    type="checkbox"
                    checked={!!checked[it.id]}
                    onChange={() => onToggle(it.id)}
                  />
                  <span style={{ marginLeft: 8 }}>{it.label}</span>
                  <span style={{ marginLeft: 8, opacity: 0.6 }}>({it.type})</span>
                </label>
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}
