import { useEffect, useState } from 'react';
import RoadmapChecklist from '../components/RoadmapChecklist';

/**
 * Knowledge 페이지
 * - 위쪽: 기존 Knowledge 화면(수정하지 말고 그대로 유지)
 * - 아래쪽: AI 생성 체크리스트 섹션 (localStorage.lastRoadmap에서 불러옴)
 */
export default function Knowledge() {
  // ✅ AI 체크리스트 상태
  const [goal, setGoal] = useState('');
  const [roadmap, setRoadmap] = useState(null);
  const [checked, setChecked] = useState({});

  // 페이지 최초 진입 시 AddGoals에서 저장해둔 lastRoadmap 복원
  useEffect(() => {
    const saved = localStorage.getItem('lastRoadmap');
    if (saved) {
      try {
        const data = JSON.parse(saved);
        setGoal(data.goal || '');
        setRoadmap(data.roadmap || null);
        setChecked(data.checked || {});
      } catch {
        console.error('Invalid lastRoadmap JSON');
      }
    }
  }, []);

  // 체크 토글 시 상태 + lastRoadmap 동기화
  const onToggle = (id) => {
    const next = { ...checked, [id]: !checked[id] };
    setChecked(next);
    localStorage.setItem('lastRoadmap', JSON.stringify({
      goal,
      roadmap,
      checked: next
    }));
  };

  // (선택) 내보내기/가져오기
  const onExport = () => {
    const blob = new Blob([JSON.stringify({ goal, roadmap, checked }, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.download = `roadmap.json`;
    a.href = url;
    a.click();
    URL.revokeObjectURL(url);
  };

  const onImport = async (e) => {
    const file = e.target.files?.[0];
    if (!file) return;
    const text = await file.text();
    try {
      const data = JSON.parse(text);
      if (!data.roadmap || !data.checked) throw new Error('Invalid file');
      setGoal(data.goal || '');
      setRoadmap(data.roadmap);
      setChecked(data.checked);
      localStorage.setItem('lastRoadmap', JSON.stringify(data));
    } catch {
      alert('Invalid JSON file.');
    }
  };

  return (
    <div className="gs-page">
      <main className="gs-container">
        {/* ============================== */}
        {/* ✅ 기존 Knowledge 페이지 콘텐츠 */}
        {/* 아래 주석 사이에 현재 사용 중인 기존 JSX를 그대로 넣으세요 */}
        {/* ============================== */}
        {/* === 기존 콘텐츠 시작 === */}
        <header className="gs-hero">
          <h1>Knowledge</h1>
          <p className="gs-sub">
            (여기는 기존 화면 예시입니다. 실제로 사용 중인 컴포넌트/텍스트/카드/테이블 등을 그대로 유지하세요.)
          </p>
        </header>

        {/* 예시 카드 (필요 없으면 삭제) */}
        <section className="gs-card" style={{ marginTop: 16 }}>
          <h2>Existing Knowledge Section</h2>
          <p style={{ opacity: 0.85 }}>
            여기에 기존에 구현하신 지식 입력/표시 UI, 설명, 네비게이션 버튼 등이 들어갑니다.
          </p>
        </section>
        {/* === 기존 콘텐츠 끝 === */}

        {/* ============================== */}
        {/* ✅ AI 체크리스트 섹션 (페이지 하단에 추가) */}
        {/* ============================== */}
        <section className="gs-card" style={{ marginTop: 24 }}>
          <h2 style={{ marginBottom: 12 }}>AI-generated Knowledge Checklist</h2>

          {!roadmap ? (
            <p style={{ opacity: 0.8 }}>
              No checklist found. Please go back to <strong>Add Goals</strong> and press
              <em> Continue to Knowledge</em> to generate it.
            </p>
          ) : (
            <RoadmapChecklist
              roadmap={roadmap}
              checked={checked}
              onToggle={onToggle}
              onExport={onExport}
              onImport={onImport}
            />
          )}
        </section>
      </main>
    </div>
  );
}
